.parallax { 

  /* The image used */
  background-image: url("resources/sailplot.png");

  /* Set a specific height */
  height: 100vh; 

  /* Create the parallax scrolling effect */
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
}

.welcome {
  position: absolute;
  left: 0;
  top: 25vh;
  width: 100vw;
  text-align: center;
  color: #000;
}

.welcome span.title {
  font-family: 'Bitter', serif;
  color: #000;
  font-size: 48px;
  letter-spacing: 10px;
  line-height: 1.6;
}

.welcome span.surrounding {
  font-family: 'Bitter', serif;
  color: #000;
  font-size: 24px;
  letter-spacing: 5px;
}

.welcome span.downarrow {
  font-size: 42px;
}


.downbutton {
  background: rgba(0,0,0,0);
  border: none;
  outline: none;
  color: #1BC7DC;
  text-align: center;
  font-size: 42px;
}

d-title.d-title {
  display: none;
}

.greeting {
  min-height: 50vh;
  padding-top: 25vh;
  font-size: 20px;
  text-align: center;
  line-height: 1.25;
}



